sys_variable_value.value:
(function execute(inputs, outputs) {
 var plBuilder = new PayloadBuilder();
  var payload = plBuilder.createIncidentPayload(inputs.incident_sys_id);
  payload.u_api_key = inputs.api_key;
  payload.u_priority = inputs.priority;
  payload.u_state = inputs.state;
  outputs.rest_body = JSON.stringify(payload);
})(inputs, outputs);

