sys_variable_value.value:
(function execute(inputs, outputs) {
  var payload = {correlation_id:inputs.incident_id};
    outputs.rest_body = JSON.stringify(payload);
    outputs.incident_sys_id = inputs.correlation_id;
})(inputs, outputs);

