sys_variable_value.value:
(function execute(inputs, outputs) {

    var answer = '';
        var ep = new GlideRecord('x_snc_con_endpoint');
        ep.addActiveQuery();
        ep.addQuery('company', inputs.api_key);
        ep.query();
        if (ep.next()) {
            answer = ep;
        }

   outputs.endpoint = answer;

})(inputs, outputs);

